class RomanNumerals {

	def ARABIC_TO_ROMAN = [
		10 : 'X',
		5  : 'V',
		1  : 'I',
	]

    def translate(int number) {
    	def roman = ''
    	ARABIC_TO_ROMAN.each { 
    		while (number > (it.key - 1)) {
    			roman += it.value
    			number -= it.key
    		}
    	}
        roman
    }

  
}
